import { ScaleFromCenterAndroid } from '@react-navigation/stack/lib/typescript/src/TransitionConfigs/TransitionPresets';
import * as React from 'react';
import { StyleSheet } from 'react-native';
import { widthPercentageToDP } from "react-native-responsive-screen"
import { Button, Form, InputGroup, Col } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

import { Card, withTheme } from 'react-native-elements';
import { Formik, Field, ErrorMessage } from "formik";
import { useFormik } from 'formik';
import Swal from 'sweetalert2'
import * as yup from 'yup';

const schema = yup.object({
  nombre: yup.string()
  .max(30, "Máximo 30 carácteres")
  .required("El campo es requerido"),
  direccion: yup.string()
  .required("El campo es requerido"),
  telefono: yup.number()
  .required("El campo es requerido"),
  tendero: yup.number()
  .required("El campo es requerido"),
  hora: yup.number()
  .required(),
  tipo: yup.string()
  .required(),
});

export default function TabTwoScreen() {
    return (
      <Card>
      <Formik
      validationSchema={schema}
      onSubmit={console.log}
      initialValues={{
        nombre: 'Mark',
        direccion: 'Calle 1 #123',
      }}
    >
      {({
        handleSubmit,
        handleChange,
        handleBlur,
        values,
        touched,
        isValid,
        errors,
      }) => (
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Row>
            <Form.Group as={Col} md="4" controlId="validationFormik101">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                name="nombre"
                value={values.nombre}
                onChange={handleChange}
                isValid={touched.nombre && !errors.nombre}
              />
              <Form.Control.Feedback tooltip>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationFormik102">
              <Form.Label>Direccion</Form.Label>
              <Form.Control
                type="text"
                name="direccion"
                value={values.direccion}
                onChange={handleChange}
                isValid={touched.direccion && !errors.direccion}
              />

              <Form.Control.Feedback tooltip>Looks good!</Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="4" controlId="validationFormikUsername2">
              <Form.Label>Telefono</Form.Label>
              <InputGroup>
                <InputGroup.Prepend>
                  <InputGroup.Text id="inputGroupPrepend">+34</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control
                  type="number"
                  placeholder="telefono"
                  aria-describedby="inputGroupPrepend"
                  name="telefono"
                  value={values.telefono}
                  onChange={handleChange}
                  isInvalid={!!errors.telefono}
                />
                <Form.Control.Feedback type="invalid" tooltip>
                  {errors.telefono}
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
          </Form.Row>
          <Form.Row>
            <Form.Group as={Col} md="6" controlId="validationFormik103">
              <Form.Label>Tendero</Form.Label>
              <Form.Control
                type="text"
                placeholder="tendero"
                name="tendero"
                value={values.tendero}
                onChange={handleChange}
                isInvalid={!!errors.tendero}
              />

              <Form.Control.Feedback type="invalid" tooltip>
                {errors.tendero}
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group as={Col} md="3" controlId="validationFormik104">
              <Form.Label>Hora</Form.Label>
              <Form.Group controlId="exampleForm.SelectCustom">
                <Form.Control as="select" custom>
                  <option>1</option>
                  <option>2</option>
                  <option>3</option>
                  <option>4</option>
                  <option>5</option>
                </Form.Control>
              </Form.Group>
              {/* <Form.Control
                type="number"
                placeholder="hora"
                name="hora"
                value={values.hora}
                onChange={handleChange}
                isInvalid={!!errors.hora}
              /> */}
              <Form.Control.Feedback type="invalid" tooltip>
                {errors.hora}
              </Form.Control.Feedback>
            </Form.Group>

            <Form.Group as={Col} md="3" controlId="validationFormik105">
              <Form.Label>Tipo</Form.Label>
              <Form.Group controlId="exampleForm.SelectCustom">
                <Form.Control as="select" custom>
                  <option>A</option>
                  <option>B</option>
                </Form.Control>
            </Form.Group>
              {/* <Form.Control
                type="text"
                placeholder="tipo"
                name="tipo"
                value={values.tipo}
                onChange={handleChange}
                isInvalid={!!errors.tipo}
              /> */}
              <Form.Control.Feedback type="invalid" tooltip>
                {errors.tipo}
              </Form.Control.Feedback>
            </Form.Group>
          </Form.Row>

          <Form.Group>
            <Form.Check
              required
              name="terms"
              label="Agree to terms and conditions"
              onChange={handleChange}
              isInvalid={!!errors.terms}
              feedback={errors.terms}
              id="validationFormik106"
              feedbackTooltip
            />
          </Form.Group>
          <Button type="submit">Registrar</Button>
        </Form>
      )}
    </Formik>
    </Card>
        );
      }
