import React from 'react';
import { StyleSheet, SafeAreaView, TextInput, Button } from 'react-native';

import { Text, View } from './Themed';

import { withAuthenticator } from 'aws-amplify-react-native';


import Components_SingOut from './Components_SingOut';

// This is the part for the suscription 
import { API, graphqlOperation } from 'aws-amplify';
import { onCreateMessage } from '../src/graphql/subscriptions';
import { createMessage } from "../src/graphql/mutations";
import { listMessages } from "../src/graphql/queries";

import { Authenticator, SignIn } from 'aws-amplify-react-native';

import AsyncStorage from '@react-native-async-storage/async-storage';

function Components_Login({ path }: { path: string }) {

  const [message, onChangeMessage] = React.useState('');

  // Subscribe to creation of Todo
  const subscription = API.graphql(
    graphqlOperation(onCreateMessage)
  ).subscribe({
    next: (todoData: any) => {
      console.log("todoData", todoData);
      // Do something with the data
    }
  });

  // // Stop receiving data updates from the subscription
  // subscription.unsubscribe();

  return (
    <View>
      <Components_SingOut path="/screens/Components_Login.tsx" />
      <SafeAreaView>
        <Text style={styles.title}>Message</Text>
        <View style={{ width: 250 }}>
          <TextInput
            multiline={true}
            numberOfLines={20}
            maxLength={300}
            style={styles.input}
            onChangeText={Text => onChangeMessage(Text)}
            placeholder="Message"
          />
        </View>
        <Button
          onPress={() => sendMessage(message)}
          title="Send message"
          color="#ff9900" />
      </SafeAreaView>
    </View>
  );
}

async function sendMessage(message: string) {
  console.log(`This is the message: ${message}`);
  const username = await AsyncStorage.getItem("CognitoIdentityServiceProvider.nskca2jku91u87p31ug78tk2o.LastAuthUser")
  console.log('username', username)
  const send_message = {
    username: "Emanuel",
    message: message,
  };
  console.log("Se ejecuto la funcion de crear mensaje")
  return await API.graphql(graphqlOperation(createMessage, { input: send_message }));
}



const styles = StyleSheet.create({
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    textAlign: 'center',
    paddingTop: 60,
    paddingBottom: 20,
  },
  input: {
    height: 80,
    margin: 12,
    borderWidth: 2,
    padding: 10,
    borderColor: '#ffe6bf',
    borderRadius: 10,
  },
  buttonText: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#FFFFFF"
  },
  button: {
    backgroundColor: '#33AAFF',
    borderWidth: 10,
    borderRadius: 20,
    borderColor: "#33AAFF",
    padding: 5,
  }
});


export default withAuthenticator(Components_Login);
