import React from 'react';
import { StyleSheet } from 'react-native';

import { Text, View } from './Themed';

// This is the part for the suscription 
import { API, graphqlOperation } from 'aws-amplify';
import { onCreateMessage } from '../src/graphql/subscriptions';
import { createMessage } from "../src/graphql/mutations";
import { listMessages } from "../src/graphql/queries";

export default function Components_Chat({ path }: { path: string }) {
  getData();
  return (
    <View style={{width: '95%'}}>
      <Text style={styles.title}>This is the chat</Text>
    {/* Contenedor de texto del usuario principal */}
      <View style={styles.container_message_principal}>
        <View style={styles.card_message_principaluser}>
          <Text style={styles.username}>Name of user</Text>
          <Text style={styles.message_text}>Message of the user</Text>
        </View>
      </View>
    {/* Contenedor de texto del usuario secundario */}
    {/* {API.graphql(graphqlOperation(listMessages)).then((evt: any) => {
      evt.data.listMessages.items.map((todo: any, i: any) => {
          console.log("todo.username",todo.username,"todo.message", todo.message);
          return(
            <View>
              <Text style={styles.username}>Name of user</Text>
            </View>
          )
      });
  })} */}
  {
      [1,2,3,4].map((todo: any, i: any) => {
        return(
          <View>
            <Text style={styles.username}>Name of user</Text>
          </View>
        )
      })
  }
      <View style={styles.container_message_second}>
        <View style={styles.card_message_seconduser}>
          <Text style={styles.username}>Name of user</Text>
          <Text style={styles.message_text}>Message of the user</Text>
        </View>
      </View>

    </View>
  );
}

async function getData() {
  API.graphql(graphqlOperation(listMessages)).then((evt: any) => {
      evt.data.listMessages.items.map((todo: any, i: any) => {
          console.log("todo.username",todo.username,"todo.message", todo.message)
      });
  });
}


const styles = StyleSheet.create({
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    backgroundColor: '#ff9900',
    borderRadius: 10,
    textAlign: 'center',
  },
  card_message_principaluser: {
    backgroundColor: '#ff9900',
    borderRadius: 10,
    padding: 10,
    borderWidth: 2,
    borderColor: '#ffb445',
  },
  message_text: {
    fontSize: 18,
  },
  username: {
    fontSize: 13,
    fontWeight: 'bold',
  },
  container_message_principal: {
    backgroundColor: '#fff4e3',
    alignItems: 'flex-end',
    justifyContent: 'flex-end',
    paddingTop: 10,
    paddingEnd: 10,
  },
  container_message_second: {
    backgroundColor: '#fff4e3',
    alignItems: 'flex-start',
    justifyContent: 'flex-end',
    paddingStart: 10,
    paddingBottom: 10,
    paddingTop: 10,
  },
  card_message_seconduser: {
    backgroundColor: '#ffcc82',
    borderRadius: 10,
    padding: 10,
  },
});
