import * as WebBrowser from 'expo-web-browser';
import React, { Component } from 'react';
import { StyleSheet, SafeAreaView, TextInput, TouchableHighlight} from 'react-native';

import { Text, View } from './Themed';

import { withAuthenticator } from 'aws-amplify-react-native';

// import { Auth } from 'aws-amplify';


// import TabChat from '../screens/TabChat';

import Components_SingOut from './Components_SingOut';

class Button extends Component {
  render() {
    return(
      <TouchableHighlight>
        <View style={styles.button}>
          <Text style={styles.buttonText}>SEND MESSAGE</Text>
        </View>
      </TouchableHighlight>
    )
  }
}

function Components_Login({ path }: { path: string }) {

  const [message, onChangeMessage] = React.useState('');

  return (
    <View>
      <Components_SingOut path="/screens/Components_Login.tsx"/>
      <SafeAreaView>
        <Text style={styles.title}>Message</Text>
        <View style={{width: 250}}>
        <TextInput
        multiline={true}
        numberOfLines={20}
        maxLength={300}
        style={styles.input}
        onChangeText={Text => onChangeMessage(Text)}
        placeholder="Message"
        />
        <Text>{message}</Text>
        </View>
        <Button
          onPress={sendMessage}
          title="Send message"
          color="#ff9900" />
      </SafeAreaView>
    </View>
  );
}

function sendMessage() {
  console.log("Send Message....")
}


const styles = StyleSheet.create({
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    textAlign: 'center',
    paddingTop: 60,
    paddingBottom: 20,
  },
  input: {
    height: 80,
    margin: 12,
    borderWidth: 2,
    padding: 10,
    borderColor: '#ffe6bf',
    borderRadius: 10,
  },
  buttonText: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#FFFFFF"
  },
  button: {
    backgroundColor: '#33AAFF',
    borderWidth: 10,
    borderRadius: 20,
    borderColor: "#33AAFF",
    padding: 5,
  }
});


export default withAuthenticator(Components_Login);
// export default Components_Login;
