import React from 'react';
import { StyleSheet, ScrollView } from 'react-native';

import { Text, View } from './Themed';

// This is the part for the suscription 
import { API, graphqlOperation, Auth } from 'aws-amplify';
import { onCreateMessage } from '../src/graphql/subscriptions';
import { createMessage } from "../src/graphql/mutations";
import { listMessages } from "../src/graphql/queries";

import { useEffect } from 'react';

import AsyncStorage from '@react-native-async-storage/async-storage';

export default function Components_Chat({ path }: { path: string }) {

  const [data_message, onChangeDataMessage] = React.useState([]);
  const [validator, onChangeValidator] = React.useState(true);
  const [usernamelogin, onChangeUsernameLogin] = React.useState(Auth.currentAuthenticatedUser().then((a) => console.log(a)));

  useEffect(() => {
    // if (validator) {
    //   getData()
    //   onChangeValidator(false)
    // }
    getData()
  },[])

  async function getData() {
    API.graphql(graphqlOperation(listMessages)).then((evt: any) => {
      console.log("evt.data.listMessages.items", evt.data.listMessages.items);
      return (onChangeDataMessage(evt.data.listMessages.items));
    });
  }


  return (
    <View style={{ width: '95%', height: 500 }}>
      <Text style={styles.title}>This is the chat</Text>
      <ScrollView>
        {/* Contenedor de texto del usuario principal */}
        {/* <View style={styles.container_message_principal}>
          <View style={styles.card_message_principaluser}>
            <Text style={styles.username}>Name of user</Text>
            <Text style={styles.message_text}>Message of the user</Text>
          </View>
        </View> */}
        {/* Contenedor de texto del usuario secundario */}
        {
          data_message.map((todo: any, i: any) => {
            if (todo.username == usernamelogin) {
              return (
                <View style={styles.container_message_principal}>
                  <View style={styles.card_message_principaluser}>
                    <Text style={styles.username}>{todo.username}</Text>
                    <Text style={styles.message_text}>{todo.message}</Text>
                  </View>
                </View>
              )
            } else if (todo.username != usernamelogin) {
              Auth.currentAuthenticatedUser().then((a) => console.log(a))
              return (
                <View style={styles.container_message_second}>
                  <View style={styles.card_message_seconduser}>
                    <Text style={styles.username}>{todo.username}</Text>
                    <Text style={styles.message_text}>{todo.message}</Text>
                  </View>
                </View>
              )
            }
          })
        }
        {/* <View style={styles.container_message_second}>
        <View style={styles.card_message_seconduser}>
          <Text style={styles.username}>Name of user</Text>
          <Text style={styles.message_text}>Message of the user</Text>
        </View>
      </View> */}

      </ScrollView>
    </View>
  );


}

async function getData() {
  API.graphql(graphqlOperation(listMessages)).then((evt: any) => {
    return (evt.data.listMessages.items);
  });
}


const styles = StyleSheet.create({
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    backgroundColor: '#ff9900',
    borderRadius: 10,
    textAlign: 'center',
  },
  card_message_principaluser: {
    backgroundColor: '#ff9900',
    borderRadius: 10,
    padding: 10,
    borderWidth: 2,
    borderColor: '#ffb445',
  },
  message_text: {
    fontSize: 18,
  },
  username: {
    fontSize: 13,
    fontWeight: 'bold',
  },
  container_message_principal: {
    backgroundColor: '#fff4e3',
    alignItems: 'flex-end',
    justifyContent: 'flex-end',
    paddingTop: 10,
    paddingEnd: 10,
  },
  container_message_second: {
    backgroundColor: '#fff4e3',
    alignItems: 'flex-start',
    justifyContent: 'flex-end',
    paddingStart: 10,
    paddingBottom: 10,
    paddingTop: 10,
  },
  card_message_seconduser: {
    backgroundColor: '#ffcc82',
    borderRadius: 10,
    padding: 10,
  },
});
