import { Reducer } from 'redux';
import {
  CREATE_PRODUCER,
  CREATE_PRODUCER_SUCCESS,
  CREATE_PRODUCER_ERROR,
} from '../actions';
import { 
  ProcessAction 
} from './actions';



export interface ProcessState {
  readonly producer: string
  readonly loading: boolean
  readonly error: string
}

const INIT_STATE = {
  producer: '',
  loading: false,
  error: ''
};

export const processReducer: Reducer<ProcessState, ProcessAction> = (
  state = INIT_STATE, 
  action: ProcessAction
) => {
  switch (action.type) {
    case CREATE_PRODUCER:
      return { ...state, loading: true, error: '' };
    case CREATE_PRODUCER_SUCCESS:
      return { ...state, loading: false, producer: action.payload.id, error: '' };
    case CREATE_PRODUCER_ERROR:
      return { ...state, loading: false, producer: '', error: action.payload.message };
    default: return { ...state };
  }
};
